	//针对逾期类的解析
	@SuppressWarnings("unchecked")
	private static final Map<String, Object> overdueConvert(Map<String,Object> detailMap){
		LinkedHashMap<String, Object> riskDetail = new LinkedHashMap<String, Object>();
		riskDetail.put("description", detailMap.get("description"));//规则描述
		riskDetail.put("discreditTimes", detailMap.get("discredit_times"));//规则描述
		List<Map<String,Object>> overdueMapList = (List<Map<String,Object>>)detailMap.get("overdue_details");
		List<String> list = new ArrayList<String>();
		if (overdueMapList!=null) {
			for (int i = 0; i < overdueMapList.size(); i++) {
				Map<String, Object> overdueMap = overdueMapList.get(i);
				String amount = (String) overdueMap.get("overdue_amount_range");
				Integer overdueCount = (Integer) overdueMap.get("overdue_count");
				String overdueDayRange = (String) overdueMap.get("overdue_day_range");
				String overdueTime = (String) overdueMap.get("overdue_time");
				list.add("逾期金额:" + amount + " 逾期笔数:" + overdueCount + " 逾期天数:"
						+ overdueDayRange + " 逾期入库时间:" + overdueTime);
			}
			riskDetail.put("overdue", list);
		}
		return riskDetail;
	}
	
	//针对频度规则的解析
	@SuppressWarnings("unchecked")
	private static final Map<String, Object> frequencyDetailConvert(Map<String,Object> detailMap){
		LinkedHashMap<String, Object> riskDetail = new LinkedHashMap<String, Object>();
		List<Map<String,Object>> frequencyDetailList = (List<Map<String,Object>>)detailMap.get("frequency_detail_list");
		List<Map<String,Object>> list = new ArrayList<Map<String,Object>>();
		for (int i = 0; i < frequencyDetailList.size(); i++) {
			Map<String, Object> frequencyDetailMap = frequencyDetailList.get(i);
			Map<String, Object> map = new LinkedHashMap<String, Object>();
			String detail = (String) frequencyDetailMap.get("detail");
			List<String> dataList = (List<String>) frequencyDetailMap.get("data");
			map.put("detail", detail);
			map.put("data", dataList);
			list.add(map);
		}
		riskDetail.put("detailList", list);
		return riskDetail;
	}
