	//针对多维度规则解析
	@SuppressWarnings("unchecked")
	private static final Map<String, Object> multiDimensionConvert(Map<String,Object> detailMap){
		LinkedHashMap<String, Object> riskDetail = new LinkedHashMap<String, Object>();
		riskDetail.put("description", detailMap.get("description"));
		riskDetail.put("total", detailMap.get("platform_count"));//总个数
		List<Map<String,Object>> platformDetailList = (List<Map<String,Object>>)detailMap.get("platform_detail");
		List<String> detailList = new ArrayList<String>();
		if (platformDetailList!=null) {
			for (int i = 0; i < platformDetailList.size(); i++) {
				Map<String, Object> platformDetailMap = platformDetailList.get(i);
				Integer count = (Integer) platformDetailMap.get("count");
				String name = (String) platformDetailMap.get("industry_display_name");
				detailList.add(name + ":" + count);
			} 
		}
		riskDetail.put("detail", detailList);//详情数组
		List<Map<String,Object>> platformDetailDimension = (List<Map<String,Object>>)detailMap.get("platform_detail_dimension");
		List<Map<String,Object>> dimensionList = new ArrayList<Map<String,Object>>();
		for (int i = 0; i < platformDetailDimension.size(); i++) {
			Map<String, Object> dimensionMap = platformDetailDimension.get(i);
			String dimension = (String) dimensionMap.get("dimension");
			Integer count = (Integer) dimensionMap.get("count");
			List<String> industryList = new ArrayList<>();
			List<Map<String,Object>> list = (List<Map<String, Object>>) dimensionMap.get("detail");
			for (int j = 0; j < list.size(); j++) {
				Map<String, Object> detail = list.get(j);
				String industry_name = (String) detail.get("industry_display_name");
				Integer industry_count = (Integer) detail.get("count");
				industryList.add(industry_name+":"+industry_count);
			}
			Map<String, Object> map = new LinkedHashMap<>();
			map.put("dimension", dimension);
			map.put("count", count);
			map.put("industryList", industryList);
			dimensionList.add(map);
		}
		riskDetail.put("dimension", dimensionList);//多维度信息
		return riskDetail;
	}
	
	//针对法院的解析
	@SuppressWarnings("unchecked")
	private static final Map<String, Object> courtDetailConvert(Map<String,Object> detailMap){
		LinkedHashMap<String, Object> riskDetail = new LinkedHashMap<String, Object>();
		riskDetail.put("description", detailMap.get("description"));//规则描述
		riskDetail.put("riskType", detailMap.get("fraud_type_display_name"));
		riskDetail.put("matchField", detailMap.get("hit_type_display_name"));
		List<Map<String,Object>> courtDetailsList = (List<Map<String,Object>>)detailMap.get("court_details");
		for (int i = 0; i < courtDetailsList.size(); i++) {
			Map<String, Object> courtDetailMap = courtDetailsList.get(i);
			riskDetail.put("executedName"+(i+1), courtDetailMap.get("executed_name"));
			riskDetail.put("riskType"+(i+1), courtDetailMap.get("fraud_type_display_name"));
			riskDetail.put("propertyValue"+(i+1), courtDetailMap.get("value"));
			riskDetail.put("executeCourt"+(i+1), courtDetailMap.get("execute_court"));
			riskDetail.put("caseDate"+(i+1), courtDetailMap.get("case_date"));
			riskDetail.put("evidenceCourt"+(i+1), courtDetailMap.get("evidence_court"));
			riskDetail.put("carryOut"+(i+1), courtDetailMap.get("carry_out"));
			riskDetail.put("specifcSituation"+(i+1), courtDetailMap.get("specific_circumstances"));
			riskDetail.put("caseCode"+(i+1), courtDetailMap.get("case_code"));
		}
		return riskDetail;
	}
	
